package Generated::Strings;
use Exporter;
our @ISA = qw( Exporter );
our @EXPORT_OK = qw( get_string string_lang gettext dgettext );

my $lang = 'en';
my $strings = {};

sub get_string {
    my ( $string, $lang ) = @_;
    my $ret = $strings->{$lang}{$string};
    return $string unless defined($ret);
    return $ret;
}

sub string_lang {
    $lang = $_[0] if $_[0];
    return $lang;
}

sub gettext {
    return get_string( $_[0], $lang );
}

sub dgettext {
    return get_string( $_[1], $lang );
}
    
